import React from 'react';
import '../App.css';
import Grid from '@mui/material/Grid';
import { useNavigate, Navigate, Link } from 'react-router-dom';
// import axios from 'axios';
import {
  Typography,
  Container,
  List,
  ListItem,
  Divider,
  ListItemText,
  ListItemButton,
} from '@mui/material';
import { styled, useTheme } from '@mui/system';
import { createTheme } from '@mui/material/styles';

import projectsData from '../data/ProjectsData';

const ProgButton = styled('button')({
  backgroundColor: '#0171e3',
  width: '430px',
  height: '50px',
  margin: '10px',
  color: 'white',
  padding: '5px 10px',
  border: 'none',
  borderRadius: '5px',
  alignSelf: 'center',
  cursor: 'pointer',
});

const Box = styled('div')({
  display: 'flex',
  height: '100%',
  border: '1px solid #6D8472',
  borderRadius: '15px',
  padding: '10px'
});

const ProjectsSection = styled('div')({
  display: 'flex',
  height: '100%',
})

const Item = styled('button')({
  backgroundColor: '#09110B',
  border: '1px solid #6D8472',
  color: '#e8e5df',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'flex-start',
  textAlign: 'left',
  width: '90%',
  padding: '20px',
  margin: '10px'
});

const ProjectsBox = ({ id, title, description, onClick, selected }) => (
  <Item className="projects-box" id={id} onClick={onClick} selected={selected}>
    <Typography sx={{ fontSize: "2rem", fontWeight: "500" }}>
      {title}
    </Typography>
    <Typography sx={{ fontSize: ".875rem" }}>
      {description}
    </Typography>
  </Item>
)

function Projects({ token, setTokenFunction }) {
  const navigate = useNavigate();
  const theme = useTheme();

  const [selectedProject, setSelectedProject] = React.useState(null);
  const [selection, setSelection] = React.useState(new Set());
  const [projectURL, setProjectURL] = React.useState(null);

  // const handleProjectClick = (id, url) => {
  //   if (selection.has(id)) {
  //     selection.delete(id);
  //   } else {
  //     selection.add(id);
  //   }
  //   setSelection(new Set(selection));
  //   setSelectedProject(id === selectedProject ? null : id);
  //   setProjectURL(url);
  // }

  let btn = document.getElementsByClassName("btn");

  for (var i = 0; i < btn.length; i++) {
    (function (index) {
      btn[index].addEventListener("click", function () {
        console.log("Clicked Button: " + index);

        let isPresent = false;

        //   Check if the class is present or not
        this.classList.forEach(function (e, i) {
          if (e == "button-focus") {
            isPresent = true;
          } else {
            isPresent = false;
          }
        });

        //   toggle the presence of class on the basis of the isPresent variable
        if (isPresent) {
          this.classList.remove("button-focus");
        } else {
          this.classList.add("button-focus");
        }
      });
    })(i);
  }

  React.useEffect(() => {
    if (selectedProject) {
      if (selectedProject === "presentation-project") {
        console.log("presto");
      } else if (selectedProject === "toohak-project") {
        console.log("toohak");
      } else if (selectedProject === "forum-project") {
        console.log("qanda");
      } else if (selectedProject === "sneaky-saving-strategies-project") {
        console.log("sneaky");
      }
    }
  }, [selectedProject])

  // React.useEffect(() => {
  //   if(projectURL) {
  //     window.location.href = projectURL
  //   }
  // })

  const goToCart = () => {
    navigate('/cart')
  }

  return <>
    <div sx={{ display: "flex", flexDirection: "column", alignContent: "center", justifyContent: "center"}}>
      <Container component="main" maxWidth="md" sx={{ display: "flex", flexDirection:"column", paddingBottom: "50px" }}>
      </Container>
      <Grid
        sx={{
          flexGrow: 1, height: "800px",
          px: { xs: "10px", sm: "25px", md: "100px", lg: "150px", xl: "200px" }
        }}
        container spacing={2}
      >
        <Grid item xs={8}>
          <Typography sx={{ fontSize: "1rem", color: "#a7a396" }}>
            Step <b>2</b> of <b>3</b>
          </Typography>

          <Typography sx={{ fontSize: "2rem", fontWeight: "500" }}>
            Choose Projects
          </Typography>
          <Box sx={{ height: "80%", position: "relative" }}>
            {projectURL}
            {projectURL && (
              <div
              style={{ height: "100%", cursor: "pointer", position: 'absolute', top: 0, left: 0, right: 0, bottom: 0 }}
              onClick={() => window.open(projectURL, '_blank')}
              >
              </div>
            )}
          </Box>
        </Grid>
        <Grid item xs={4}>
          <Typography id="empty-spacer1" sx={{ fontSize: "1rem", color: "#a7a396" }}>
            &nbsp;
          </Typography>
          <Typography id="empty-spacer2" sx={{ fontSize: "2rem", fontWeight: "500" }}>
            &nbsp;
          </Typography>
          <ProjectsSection sx={{ height: "80%", flexDirection:"column", padding:"0", overflowY: "scroll" }}>
            <Typography sx={{ fontSize: "2rem", fontWeight: "500" }}>
              Projects.
              <Typography component="span" sx={{ fontSize: "2rem", fontWeight: "500", color: "#a7a396" }}>
                Which interests you?
              </Typography>
            </Typography>
            {projectsData.map((project) =>(
              <ProjectsBox
                key={project.id}
                {...project}
                onClick={() => handleProjectClick(project.id, project.url)}
                selected={selection.has(project.id)}
              />
            ))}
            <ProjectsSection sx={{ flexDirection: "row", alignItems: "center" }}>
              <input type="checkbox" id="add-all-check"/>
              <span>
                Add all to cart?
              </span>
            </ProjectsSection>
          </ProjectsSection>
        </Grid>
        <div style={{ display: 'flex', justifyContent: 'center', width: '100%' }}>
          <ProgButton onClick={goToCart}>Next</ProgButton>
        </div>
      </Grid>
    </div>

  </>
}

export default Projects;
