import React from 'react';
import '../App.css';
import Grid from '@mui/material/Grid';
import { useNavigate, Navigate, Link } from 'react-router-dom';
// import axios from 'axios';
import {
  Typography,
  Container,
  List,
  ListItem,
  Divider,
  ListItemText,
  ListItemButton,
} from '@mui/material';
import { styled, useTheme } from '@mui/system';
import { createTheme } from '@mui/material/styles';

import projectsData from '../data/ProjectsData';

const ProgButton = styled('button')({
  backgroundColor: '#0171e3',
  width: '430px',
  height: '50px',
  margin: '10px',
  color: 'white',
  padding: '5px 10px',
  border: 'none',
  borderRadius: '5px',
  alignSelf: 'center',
  cursor: 'pointer',
});

const Box = styled('div')({
  display: 'flex',
  height: '100%',
  border: '1px solid #6D8472',
  borderRadius: '15px',
  padding: '10px'
});

const FlexDiv = styled('div')({
  display: 'flex',
  height: '100%',
})

const Item = styled('div')(({ selected }) => ({
  backgroundColor: selected ? '#6D8472' : '#09110B',
  border: '1px solid #6D8472',
  color: selected ? '#09110B' : '#e8e5df',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'flex-start',
  textAlign: 'left',
  width: '80%',
  padding: '20px',
  margin: '10px',
  transition: 'background-color 0.2s, color 0.2s'
}));

const ProjectsBox = ({ id, title, description, onClick, selected }) => (
  <Item className="projects-box" id={id} onClick={onClick} selected={selected}>
    <Typography sx={{ fontSize: "2rem", fontWeight: "500" }}>
      {title}
    </Typography>
    <Typography sx={{ fontSize: ".875rem" }}>
      {description}
    </Typography>
  </Item>
)

const Modal = styled('div') (({ toggle, width, height }) => ({
  display: toggle ? 'block' : 'none',
  position: 'fixed',
  backgroundColor: 'rgb(90, 165, 107, 0.2)',
  zIndex: '1',
  top: '20%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  borderRadius: '20px',
  width: width,
  height: height,
}))

const ErrorModal = () => (
  <div className="error-modal">

  </div>
)

function Projects({ token, setTokenFunction }) {
  const navigate = useNavigate();
  const theme = useTheme();

  const [selectedProject, setSelectedProject] = React.useState(null);
  const [selection, setSelection] = React.useState(new Set());
  const [projectURL, setProjectURL] = React.useState(null);
  const [selectAll, setSelectAll] = React.useState(false);
  const [toggleErrorModal, setToggleErrorModal] = React.useState(false);
  const [toggleAddOnModal, setToggleAddOnModal] = React.useState(false);

  const handleProjectClick = (id, url) => {
    if (selection.has(id)) {
      selection.delete(id);
    } else {
      selection.add(id);
    }
    setSelection(new Set(selection));
    setSelectedProject(id === selectedProject ? null : id);
    setProjectURL(url);
  }

  const handleSelectAll = (event) => {
    const checked = event.target.checked;
    setSelectAll(checked);

    if (checked) {
      const allProjectIds = projectsData.map((project) => project.id);
      setSelection(new Set(allProjectIds));
    } else {
      setSelection(new Set());
    }
  }

  // open on click of next.
  const openModals = () => {
    if (selection.size != projectsData.length) {
      setToggleErrorModal(!toggleErrorModal);
    } else {
      setToggleAddOnModal(!toggleAddOnModal);
    }
  }

  React.useEffect(() => {
    let check = document.querySelectorAll("add-all-check");
    if (selection.size === 4) {
      setSelectAll(true);
    } else {
      setSelectAll(false);
    }
  }, [selection])

  const goToCart = () => {
    navigate('/cart')
  }

  return <>
    <div sx={{ display: "flex", flexDirection: "column", alignContent: "center", justifyContent: "center"}}>
      <Container component="main" maxWidth="md" sx={{ display: "flex", flexDirection:"column", paddingBottom: "50px" }}>
      </Container>
      <Grid
        sx={{
          flexGrow: 1, height: "800px",
          px: { xs: "10px", sm: "25px", md: "100px", lg: "150px", xl: "200px" }
        }}
        container spacing={2}
      >
        <Grid item xs={8}>
          <Typography sx={{ fontSize: "1rem", color: "#a7a396" }}>
            Step <b>2</b> of <b>3</b>
          </Typography>

          <Typography sx={{ fontSize: "2rem", fontWeight: "500" }}>
            Choose Projects
          </Typography>
          <Box sx={{ height: "80%", position: "relative" }}>
            {projectURL}
            {projectURL && (
              <div
              style={{ height: "100%", cursor: "pointer", position: 'absolute', top: 0, left: 0, right: 0, bottom: 0 }}
              onClick={() => window.open(projectURL, '_blank')}
              >
              </div>
            )}
          </Box>
        </Grid>
        <Grid item xs={4}>
          <Typography id="empty-spacer1" sx={{ fontSize: "1rem", color: "#a7a396" }}>
            &nbsp;
          </Typography>
          <Typography id="empty-spacer2" sx={{ fontSize: "2rem", fontWeight: "500" }}>
            &nbsp;
          </Typography>
          <FlexDiv sx={{ height: "80%", flexDirection:"column", padding:"0", overflowY: "scroll" }}>
            <Typography sx={{ fontSize: "2rem", fontWeight: "500" }}>
              Projects.
              <Typography component="span" sx={{ fontSize: "2rem", fontWeight: "500", color: "#a7a396" }}>
                Which interests you?
              </Typography>
            </Typography>
            {projectsData.map((project) =>(
              <ProjectsBox
                key={project.id}
                {...project}
                onClick={() => handleProjectClick(project.id, project.url)}
                selected={selection.has(project.id)}
              />
            ))}
            <FlexDiv sx={{ flexDirection: "row", alignItems: "center" }}>
              <input type="checkbox" id="add-all-check" checked={selectAll} onChange={handleSelectAll}/>
              <span>
                Add all to cart?
              </span>
            </FlexDiv>
          </FlexDiv>
        </Grid>
        <div style={{ display: 'flex', justifyContent: 'center', width: '100%' }}>
          <ProgButton onClick={openModals}>Next</ProgButton>
        </div>
      </Grid>
    </div>
    <Modal toggle={toggleErrorModal} width="300px" height="150px">
      <button sx={{ position: 'absolute', top: '10', right: '0'}}>
        x
      </button>
      <Typography>
        Please select all projects to continue!
      </Typography>
    </Modal>
  </>
}

export default Projects;
